function banner(){jQuery.getJSON("/admin/PageInfo/search",{url:document.URL},function(d){var a=d.dataset[0].node_id;var e=jQuery(banner_selector).position();var c=Ext.get(jQuery(banner_selector).attr("id"));var b=new Ext.Button({text:"Gestione Banner Pagina",style:{position:"absolute",zIndex:"100",top:""+e.top+"px",left:""+e.left+"px",},win:new Aybu.PageBanner.Window({page_id:a}),handler:function(){this.win.show()},});b.render(c);b.hide();jQuery(banner_selector).mouseenter(function(){jQuery(banner_selector).css("opacity","0.5");b.show()});jQuery(banner_selector).mouseleave(function(){jQuery(banner_selector).css("opacity","1");b.hide()})})};