function cacheError(){alert("Errore nella richiesta.")}function cacheToggled(a){jQuery("#toggle_cache > p > strong").html(a.state);jQuery("#toggle_cache > form > fieldset > input").attr("value",a.command);if(a.purge_enabled){jQuery("#purge_cache").css("display","block")}else{jQuery("#purge_cache").css("display","none")}}function toggleCache(a){jQuery.ajax({error:cacheError,success:cacheToggled,type:"POST",url:a});return false}function cachePurged(a){if(a.success){alert("Cache Purged")}else{cacheError()}}function purgeCache(a){jQuery.ajax({error:cacheError,success:cachePurged,type:"POST",url:a});return false};